smallButton.script:
#{form}.addOnChangeListener({
	onSetValue: function(value) {
		if (!value || !value.content)
			return; 
		if (value.content.entityId) {
			this.enable();
		} else {
			this.disable();
		}
	}
});

                #{form}.addOnChangeListener({
					onSetValue: function(value) {
						if (!value || !value.content)
							return; 
						if (value.content.entityId) {
							this.enable();
						} else {
							this.disable();
						}
					}
				});

#{form}.addOnChangeListener({
	onSetValue: function(value) {
		if (!value || !value.content)
			return; 
		if (value.content.entityId && !value.content.isActive) {
			this.enable();
		} else {
			this.disable();
		}
	}
});

                #{form}.addOnChangeListener({
					onSetValue: function(value) {
						if (!value || !value.content)
							return; 
						if (value.content.entityId && value.content.isActive) {
							this.enable();
						} else {
							this.disable();
						}
					}
				});
